import { Component, OnInit } from '@angular/core';

import { Category } from '../../category/category';
import { Movieservice } from '../movieservice.service';
import { Router } from '@angular/router';
import { AlertifyService } from '../../services/alertify-service.service';
import { FormControl, FormGroup, NgForm, NgModel, Validators } from '@angular/forms';
import { CategoryService } from 'src/app/category/category-service.service';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-movie-create',
  templateUrl: './movie-create.component.html',
  styleUrls: ['./movie-create.component.css'],
  providers: [CategoryService, Movieservice, AlertifyService]
})
export class MovieCreateComponent implements OnInit {

  categories: Category[] = [];

  model:any={categoryId:-1};

  file:any;

  urlimage:any;

 async getfile(event:any) {

    this.file=event.target.files[0];

    let formData=new FormData();

    formData.set('file', this.file);

    formData.append("file", this.file);

    formData.append("upload_preset", "angular-movies");


  // await axios.post("https://api.cloudinary.com/v1_1/angular-movies/upload",formData)

   this.uploadSignature(formData).subscribe((imageData) => {
    this.urlimage = imageData.url;

    console.log(this.urlimage);
  })


  }


  uploadSignature(vals): Observable<any>{
    let data = vals;
    return this.http.post('https://api.cloudinary.com/v1_1/sociala/upload',data)
  }

  constructor(private categoryService: CategoryService, private movieservice: Movieservice, private alertifyService: AlertifyService, private router: Router, private http:HttpClient) { }

  ngOnInit(): void {

    this.categoryService.getCategory().subscribe(data => { this.categories = data });
  }



  movieform = new FormGroup({

    title_: new FormControl("Movie title",[Validators.required, Validators.minLength(5)]),
    description_: new FormControl("Movie description",[Validators.required]),
    imageUrl_: new FormControl("../../../assets/Images/image.png",[Validators.required]),
    categoryId_: new FormControl("2",[Validators.required]),
    subimageUrl_: new FormControl("22",[Validators.required])

  });

  isFormValid() : boolean {
    return this.movieform.disabled ? true : this.movieform.valid
  }

  createMovie3(){

    const movie = {
      title: this.movieform.value.title_,
      description: this.movieform.value.description_,
      imageUrl: this.movieform.value.imageUrl_,
      isPopular: false,
      datePublished: new Date().getTime(),
      categoryId: this.movieform.value.categoryId_,
      subimageUrl: this.movieform.value.subimageUrl_,
    }

    if(this.movieform.value.title_!="" && this.movieform.value.description_!=""  && this.movieform.value.imageUrl_!=""  && this.movieform.value.categoryId_!="")
    {

      this.movieservice.postMovie(movie).subscribe(data => {console.log(data); this.router.navigate(['/movies'])});

      this.alertifyService.success(movie.title);

    }

  }

  ClearForm(){

    this.movieform.patchValue({

      title_: '',
      description_: '',
      imageUrl_: '',
      categoryId_:'-1',

    });

  }
}
<p>Create Movie</p>


<h1 style="color:red">Reactive form</h1>

<form [formGroup]="movieform" (ngSubmit)="createMovie3()">

  <div class="mb-3">
    <label for="title" class="form-label">Title</label>
    <input id="title" name="title" type="text" class="form-control"  formControlName="title_">
    <p>{{movieform.value.title_}}</p>

    <div class="alert alert-danger" *ngIf="movieform.get('title_').invalid && movieform.get('title_').touched">
      <p *ngIf="movieform.get('title_')?.errors?.['required']"> Required places </p>
      <p *ngIf="movieform.get('title_')?.errors?.['minlength']"> Minimum  5 characters write </p>
    </div>

  </div>

  <div class="mb-3">
    <label for="description" class="form-label">Description</label>
    <input  id="description" name="description" type="text" class="form-control"  formControlName="description_">
  </div>

  <div class="mb-3">
    <label for="imageUrl" class="form-label">Image Url</label>
    <input id="imageUrl" name="imageUrl" type="text" class="form-control"  formControlName="imageUrl_">
  </div>



  <div class="mb-3">
    <label for="categoryId" class="form-label">Category</label>
    <select id="categoryId" name="categoryId" class="form-select"  formControlName="categoryId_">
      <option value="-1"> Select category</option>
      <option *ngFor="let item of categories" [value]="item.id"> {{item.categoryname}}</option>
    </select>
  </div>

  <div class="mb-3" id="file-container">

    <input id="subimageUrl" name="subimageUrl" type="file" class="form-control"  formControlName="subimageUrl_"(change)="getfile($event)">

  </div>

  <div class="mb-3" *ngIf="file">

   File name: {{file.name}} <br />

   <button (click)="file=undefined"> clear file </button>

   <br>

  </div>

  <button [disabled]="!isFormValid()" type="submit" class="btn btn-success">

     Create Movie

  </button>

  <button type="submit" (click)="ClearForm()" class="btn btn-warning">

    Clear

 </button>

</form>
